import './App.css';
import React, { useState } from 'react';
import Articles from './components/Articles'


function App() {
  const [nom,setNom]=useState('bob')
  const [description,setDescription]=useState('test test')
  const [activeForm,setActiveForme]=useState(false)
  const [buttonName,setButtonName]=useState('Modifier')

    const handleClick=(e)=>{
      e.preventDefault()
      setActiveForme(!activeForm)

      if (activeForm){
       const buttonName='Modifier'
       setButtonName(buttonName)
      }
      else{
      const buttonName='Valider'
      setButtonName(buttonName)

      }
    }

    const modifNom =(e)=>{
     let value= e.target.value
     setNom(value)
    }

    const modifDescription =(e)=>{
      let value= e.target.value
      setDescription(value)
    }

    let form=null

        if(activeForm){
          form=
           <div>
             <p>Modifier nom</p>
             <input type='text' maxlength="15"  onChange={modifNom}/>
             <p>Modifier description</p>
             <input type='text' maxlength="200"  onChange={modifDescription}/>



           </div>
        }

  return (
    <div className="App">
      <Articles
      nom={nom} 
      description={description}
      form={form}
      buttonName={buttonName}
      handleClick={()=>handleClick}


      />

    </div>
  );
}

export default App;
