const Article = (props) => {
  
    let form=null
    if(props.activeForm===props.nom && !props.activeForm2){
        form=
       <div>
         <p>Modifier nom</p>
         <input type='text' maxLength="15"  onKeyUp={props.modifNom}/>
         <p>Modifier description</p>
         <input type='text' maxLength="200"  onKeyUp={props.modifDescription}/>
       </div>
    }

    return ( 
    <div className="article">
      <p>{props.nom}</p>
      <p>{props.description}</p>
      <button onClick={props.handleClick}>{props.buttonName}</button>
      <div>{form}</div>
    </div>
     );
}

export default Article ;
