import './App.css';
import React, { useState } from 'react';
import Articles from './components/Articles'

const data=[
  {name:'dessin',
  description:'cool'},
  {name:'yo',
  description:'ok'}
]
function App() {
  const [nom,setNom]=useState('bob')
  const [description,setDescription]=useState('test test')
  const [activeForm,setActiveForme]=useState(false)
  const [buttonName,setButtonName]=useState('Modifier')

    const handleClick=(e)=>{
      e.preventDefault()
      setActiveForme(!activeForm)

      if (activeForm){
       const buttonName='Modifier'
       setButtonName(buttonName)
      }
      else{
      const buttonName='Valider'
      setButtonName(buttonName)

      }
    }

    const modifNom =(e)=>{
     let value= e.target.value
     setNom(value)
    }

    const modifDescription =(e)=>{
      let value= e.target.value
      setDescription(value)
    }



  return (
    <div className="App">
      <Articles
      nom={nom} 
      description={description}
      buttonName={buttonName}
      handleClick={(e)=>handleClick(e)}
      activeForm={activeForm}
      modifNom={(e)=>modifNom(e)}
      modifDescription={(e)=>modifDescription(e)}
      />

    </div>


  );
}

export default App;
