import './App.css';
import React, { useState } from 'react';
import Article from './components/Articles';

const data=[
  {name:'dessin',
  description:'cool'},
  {name:'yo',
  description:'ok'}
]
function App() {
  const [useData,setData]=useState(data)
  const [activeForm,setActiveForme]=useState(false)
  const [buttonName,setButtonName]=useState('Modifier')
  const [inputValueNom,setInputValueNom]=useState('')

  console.log(useData)

    const handleClick=(e)=>{
      e.preventDefault()
      setActiveForme(!activeForm)

      if (activeForm){
       const buttonName='Modifier'
       setButtonName(buttonName)
      }
      else{
      const buttonName='Valider'
      setButtonName(buttonName)

      }
    }

    const modifNom =(e,key)=>{
     const data=useData
     let value= e.target.value
     data[key].name=value
     setData(data)
    }

    const modifDescription =(e,key)=>{
      const data=useData
      let value= e.target.value
      data[key].description=value
      setData(data)
    }

    const clickEnvoyer = (e)=>{
      e.preventDefault()
      //const data=useData
      const obj={name:inputValueNom}
      useData.push(obj)
        setData(useData)
    }


    const articles=Object.keys(useData).map(key=>(
      <Article
      key={key}
      nom={useData[key].name} 
      description={useData[key].description}
      buttonName={buttonName}
      handleClick={(e)=>handleClick(e)}
      activeForm={activeForm}
      modifNom={(e)=>modifNom(e,key)}
      modifDescription={(e)=>modifDescription(e,key)}
       />
    ))

  return (
    <div className="App">
     {articles}
      <p>Ajouter</p>
      <input type='text' onKeyUp={(e)=> {let nom=e.target.value; setInputValueNom(nom)}}/>
      <button onClick={clickEnvoyer}>envoyer</button>
    </div>


  );
}

export default App;
