import { useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import Header from "../components/Header";
const Axios = require('axios').default;

const Connexion =()=> {

    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')

    const envoiConnexion=(e)=>{
        e.preventDefault()
        if(email!=='/^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/'){console.log('Erreur')}
        else{ Axios.post('http://localhost:3001/api/connexion',{email:email,password:password}).then((response)=>{
            //localStorage.setItem('token',response.data.token);
            document.location.href="http://localhost:3000/gallerie"; 
             })}
       
    }

    return ( 
        <div className="connexion">
            <Header prop={ [<Link to="/inscription">Inscription</Link>,
             <Link to="/">Accueil</Link>]}/>
            <h1>Connexion</h1>
            <form>
                <div className='form'>
                <label>Adresse mail</label>
                <input type="email" id='email' onKeyUp={(e)=>{setEmail(e.target.value)}}/>
                </div>
                <div className='form'>
                <label>Mot de passe</label>
                <input type='password' id='password' onKeyUp={(e)=>{setPassword(e.target.value)}}/>
                <button type='submit' onClick={(e)=>{envoiConnexion(e)}}>Valider</button>
                </div>

            </form>
        </div>
     );
}

export default Connexion ;
