    const handleClick=(e)=>{
      e.preventDefault()
      setActiveForme(!activeForm)
    }

    let form=null

        if(activeForm){
          form=
           <div>
             <input type='text' />
              <button>Sauvegarder</button>
           </div>

        }

  return (
    <div className="App">
      <p>{nom}</p>
      <button onClick={handleClick}>Changer</button>
      <div>{form}</div>


    </div>
  );
}

export default App;
