const Article = (props) => {


  const recupUserApi=()=>{
    let token=localStorage.getItem('token')

    Axios.get('http://localhost:3001/api/get',
    {headers: {
      'Authorization': `Bearer ${token}`
    }}
    ).then((response)=>{
    console.log(response.data)
    
    })
  }
  
    let form=null
    if(props.activeForm===props.id && props.activeForm2){
        form=
       <div>
         <p>Modifier nom</p>
         <input type='text' maxLength="15"  onKeyUp={props.modifNom}/>
         <p>Modifier description</p>
         <input type='text' maxLength="100"  onKeyUp={props.modifDescription}/>
         <button onClick={props.validModif}>valider</button>
       </div>
    }
    let modifSuppr=null;




   // if(props.idUser===){}
   

    return ( 
     
    <div className="article">
      <div>
        <p className="nom">{props.nom}</p>
        <img src={props.image }/>
        <p className="description">{props.description}</p>
      </div>


      <div>{form}</div>
    </div>
     );
}

export default Article ;
