import './App.css';
import React, { useState } from 'react';
import Article from './components/Articles';

const data=[
  {name:'dessin',
  description:'cool'},
  {name:'yo',
  description:'ok'}
]
function App() {
  const [useData,setData]=useState(data)
  const [activeForm,setActiveForme]=useState('')
  const [buttonName,setButtonName]=useState('Modifier')
  const [inputValueNom,setInputValueNom]=useState('')
  const [idUser,setIdUser]=useState('')
  const [keyUser,setKeyUser]=useState('')


  console.log(useData)

    const handleClick=(e,key)=>{
      e.preventDefault()
      console.log(data[key])
      useData.forEach(elem =>{
        if(data[key].name===elem.name){
          console.log(data[key].name+'--'+elem.name)
          let test= activeForm=data[key].name

          setActiveForme(test)
          if (activeForm){
            const buttonName='Modifier'
            setButtonName(buttonName)
           }
           else{
           const buttonName='Valider'
           setButtonName(buttonName)
           }
        }

       })



      console.log(idUser)

    }

    const modifNom =(e,key)=>{
     const data=useData
     let value= e.target.value
     data[key].name=value
     setData(data)
    }

    const modifDescription =(e,key)=>{
      const data=useData
      let value= e.target.value
      data[key].description=value
      setData(data)
    }

    const addArticle = (e) =>{
    const data=e.target.value
    setInputValueNom(data)
    }
    const clickEnvoyer = (e)=>{
      e.preventDefault()
      console.log(useData)
      setData([...useData,{name:inputValueNom, description:''}])
    }

    const articles=Object.keys(useData).map(key=>(
      <Article
      key={key}
      idUser={idUser}
      nom={useData[key].name}
      description={useData[key].description}
      buttonName={buttonName}
      handleClick={(e)=>handleClick(e,key)}
      activeForm={activeForm}
      modifNom={(e)=>modifNom(e,key)}
      modifDescription={(e)=>modifDescription(e,key)}
       />
    ))

  return (
    <div className="App">
     {articles}
      <p>Ajouter</p>
      <input type='text' onKeyUp={(e)=>addArticle(e)}/>

      <button onClick={(e)=>clickEnvoyer(e)}>envoyer</button>
    </div>

  );
}

export default App;
