import './App.css';
import React, { useState } from 'react';


function App() {
  const [nom,setNom]=useState('bob')
  const [description,setDescription]=useState('test test')
  const [activeForm,setActiveForme]=useState(false)

    const handleClick=(e)=>{
      e.preventDefault()
      setActiveForme(!activeForm)
    }

    const handleChange=(e)=>{
     const value= e.target.value
     setNom(value)
    }

    let form=null

        if(activeForm){
          form=
           <div>
             <input type='text' maxlength="15"  onChange={handleChange}>Modifier nom</input>
           </div>
        }

  return (
    <div className="App">
      <p>{nom}</p>
      <p>{description}</p>
      <button onClick={handleClick}>Changer</button>
      <div>{form}</div>


    </div>
  );
}

export default App;
